[{"title":"example.js","language":"javascript","content":"// example.ts
import redis from './redis';

// Chuỗi
await redis.set('greeting', 'Hello Redis');
const greeting = await redis.get('greeting');
console.log(greeting); // Hello Redis

// Danh sách
await redis.lpush('queue', 'task1');
const task = await redis.rpop('queue');
console.log(task); // task1

// Băm
await redis.hset('user:1', { name: 'Jay', role: 'admin' });
const user = await redis.hgetall('user:1');
console.log(user); // { name: 'Jay', role: 'admin' }

// Tập hợp có thứ tự
await redis.zadd('scores', 100, 'Jay');
const scores = await redis.zrange('scores', 0, -1, 'WITHSCORES');
console.log(scores);

// Pub/Sub
redis.subscribe('news');
redis.on('message', (channel, message) => {
  console.log(`Nhận tin nhắn từ ${channel}: ${message}`);
});

const publisher = new Redis();
publisher.publish('news', 'Tin nóng!');
"}]

[{"title":"cache.js","language":"javascript","content":"import redis from './redis';

async function getUserData(userId) {
  const cacheKey = `user:${userId}`;
  const cachedData = await redis.get(cacheKey);

  if (cachedData) {
    return JSON.parse(cachedData);
  }

  // Mô phỏng truy vấn cơ sở dữ liệu
  const userData = await queryDatabase(userId);
  await redis.set(cacheKey, JSON.stringify(userData), 'EX', 3600); // Lưu vào bộ nhớ đệm trong 1 giờ
  return userData;
}

async function queryDatabase(userId) {
  // Mô phỏng truy vấn cơ sở dữ liệu
  return { id: userId, name: 'Jay', age: 30 };
}
"}]

[{"title":"javascript","language":"javascript","content":"const crypto = require('crypto');
function base64UrlEncode(str) {
  return Buffer.from(str)
    .toString('base64')
    .replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');
}

function createJWT(payload, secret) {
  const header = {
    alg: 'HS256',
    typ: 'JWT'
  };

  const encodedHeader = base64UrlEncode(JSON.stringify(header));
  const encodedPayload = base64UrlEncode(JSON.stringify(payload));

  const signature = crypto
    .createHmac('sha256', secret)
    .update(`${encodedHeader}.${encodedPayload}`)
    .digest('base64')
    .replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');

  return `${encodedHeader}.${encodedPayload}.${signature}`;
}
"}]

[{"title":"javascript","language":"javascript","content":"function verifyJWT(token, secret) {
  const [encodedHeader, encodedPayload, signature] = token.split('.');
  const expectedSignature = crypto
    .createHmac('sha256', secret)
    .update(`${encodedHeader}.${encodedPayload}`)
    .digest('base64')
    .replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');

  return expectedSignature === signature;
}
const token = createJWT(payload, secret);
const isValid = verifyJWT(token, secret);
console.log('Token is valid:', isValid);
"}]

[{"title":"javascript","language":"javascript","content":"const payload = {
  user_id: 123,
  exp: Math.floor(Date.now() / 1000) + (60 * 60) // Hết hạn sau 1 giờ
};
const token = createJWT(payload, secret);
const isValid = verifyJWT(token, secret);
const decodedPayload = JSON.parse(Buffer.from(token.split('.')[1], 'base64').toString());
const isExpired = decodedPayload.exp < Math.floor(Date.now() / 1000);
console.log('Token is valid:', isValid);
console.log('Token is expired:', isExpired);
"}]

[{"title":"javascript","language":"javascript","content":"export const sendEvent = (eventName, detail) => {
  document.dispatchEvent(new CustomEvent(eventName, { detail }));
}
export const listenEvent = (eventName, callback, context = document) => {
  context.addEventListener(eventName, callback);
  return () => context.removeEventListener(eventName, callback);
}
