[{"title":"default.conf","language":"nginx","content":"server {
    listen 80 default_server;
    server_name _;
    root /var/www/html;
    index index.html index.htm;

    location / {
        try_files $uri $uri/ =404;
    }
}
"}]

[{"title":"php-fpm.conf","language":"nginx","content":"location ~ \.php$ {
  try_files $uri =404;
  fastcgi_pass unix:/var/run/php/php8.1-fpm.sock;
  fastcgi_index index.php;
  fastcgi_param SCRIPT_FILENAME $document_root$fastcgi_script_name;
  include fastcgi_params;
}
"}]

[{"title":"upstream.conf","language":"nginx","content":"upstream myapp1 {
    ip_hash;
    server 192.168.1.10:8000;
    server 192.168.2.10:8000;
    # server 192.168.1.10:8000 weight=3;
}
"}]

[{"title":"proxy.conf","language":"nginx","content":"server {
  listen 80 default_server;
  server_name _;
  root /var/www/html;
  index index.html index.htm;

  location / {
      proxy_pass http://myapp1;
      proxy_set_header Host $host;
      proxy_set_header X-Real-IP $remote_addr;
      proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
      proxy_set_header X-Forwarded-Proto $scheme;
  }
}
"}]

[{"title":"gzip.conf","language":"nginx","content":"gzip on;
gzip_disable "msie6";
gzip_vary on;
gzip_proxied any;
gzip_comp_level 6;
gzip_types text/plain text/css application/json application/javascript text/xml application/xml application/xml+rss text/javascript;
"}]

[{"title":"redirect.conf","language":"nginx","content":"server {
    listen 80;
    server_name example.com;
    return 301 https://$host$request_uri;
}
server {
    listen 443 ssl;
    server_name example.com;
    ssl_certificate /path/to/cert.pem;
    ssl_certificate_key /path/to/key.pem;
}
"}]

