[{"title":"example.js","language":"javascript","content":"// example.ts
import redis from './redis';

// Chuỗi
await redis.set('greeting', 'Hello Redis');
const greeting = await redis.get('greeting');
console.log(greeting); // Hello Redis

// Danh sách
await redis.lpush('queue', 'task1');
const task = await redis.rpop('queue');
console.log(task); // task1

// Băm
await redis.hset('user:1', { name: 'Jay', role: 'admin' });
const user = await redis.hgetall('user:1');
console.log(user); // { name: 'Jay', role: 'admin' }

// Tập hợp có thứ tự
await redis.zadd('scores', 100, 'Jay');
const scores = await redis.zrange('scores', 0, -1, 'WITHSCORES');
console.log(scores);

// Pub/Sub
redis.subscribe('news');
redis.on('message', (channel, message) => {
  console.log(`Nhận tin nhắn từ ${channel}: ${message}`);
});

const publisher = new Redis();
publisher.publish('news', 'Tin nóng!');
"}]

[{"title":"cache.js","language":"javascript","content":"import redis from './redis';

async function getUserData(userId) {
  const cacheKey = `user:${userId}`;
  const cachedData = await redis.get(cacheKey);

  if (cachedData) {
    return JSON.parse(cachedData);
  }

  // Mô phỏng truy vấn cơ sở dữ liệu
  const userData = await queryDatabase(userId);
  await redis.set(cacheKey, JSON.stringify(userData), 'EX', 3600); // Lưu vào bộ nhớ đệm trong 1 giờ
  return userData;
}

async function queryDatabase(userId) {
  // Mô phỏng truy vấn cơ sở dữ liệu
  return { id: userId, name: 'Jay', age: 30 };
}
"}]

[{"title":"Dockerfile","language":"dockerfile","content":"# Stage 1: Build
FROM node:18-alpine AS build
WORKDIR /app

COPY package*.json ./

RUN npm ci

COPY . .

RUN npm run build

# Stage 2: Run
FROM nginx:alpine

COPY --from=build /app/build /usr/share/nginx/html
COPY nginx.conf /etc/nginx/nginx.conf

EXPOSE 3000
CMD ["nginx", "-g", "daemon off;"]
"},{"title":"nginx.conf","language":"nginx","content":"worker_processes auto;
error_log /var/log/nginx/error.log warn;
pid /var/run/nginx.pid;

events {
    worker_connections 1024;
}

http {
    include /etc/nginx/mime.types;
    default_type application/octet-stream;

    log_format main '$remote_addr - $remote_user [$time_local] "$request" '
                  '$status $body_bytes_sent "$http_referer" '
                  '"$http_user_agent" "$http_x_forwarded_for"';

    access_log /var/log/nginx/access.log main;

    server_tokens off;

    gzip on;
    gzip_types text/plain text/css application/json application/javascript text/xml application/xml application/xml+rss text/javascript;

    server {
        listen 3000;  # Lắng nghe trên cổng 3000
        server_name localhost;

        root /usr/share/nginx/html;
        index index.html;

        location / {
            try_files $uri $uri/ /index.html;
        }

        location ~* \.(js|css|png|jpg|jpeg|gif|ico|svg|woff|woff2|ttf|eot)$ {
            expires 1y;
            add_header Cache-Control "public, no-transform";
        }

        error_page 404 /404.html;
        location = /404.html {
            internal;
        }

        error_page 500 502 503 504 /50x.html;
        location = /50x.html {
            internal;
        }

        location ~ /\.ht {
            deny all;
        }

        add_header X-Frame-Options "SAMEORIGIN";
        add_header X-Content-Type-Options "nosniff";
        add_header X-XSS-Protection "1; mode=block";
        add_header Referrer-Policy "no-referrer-when-downgrade";
        add_header Content-Security-Policy "default-src 'self'; script-src 'self' 'unsafe-inline' 'unsafe-eval'; style-src 'self' 'unsafe-inline'; img-src 'self' data:; font-src 'self'; connect-src 'self'; frame-ancestors 'self';";
    }
}
"}]

