[{"title":"javascript","language":"javascript","content":"const crypto = require('crypto');
function base64UrlEncode(str) {
  return Buffer.from(str)
    .toString('base64')
    .replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');
}

function createJWT(payload, secret) {
  const header = {
    alg: 'HS256',
    typ: 'JWT'
  };

  const encodedHeader = base64UrlEncode(JSON.stringify(header));
  const encodedPayload = base64UrlEncode(JSON.stringify(payload));

  const signature = crypto
    .createHmac('sha256', secret)
    .update(`${encodedHeader}.${encodedPayload}`)
    .digest('base64')
    .replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');

  return `${encodedHeader}.${encodedPayload}.${signature}`;
}
"}]

[{"title":"javascript","language":"javascript","content":"function verifyJWT(token, secret) {
  const [encodedHeader, encodedPayload, signature] = token.split('.');
  const expectedSignature = crypto
    .createHmac('sha256', secret)
    .update(`${encodedHeader}.${encodedPayload}`)
    .digest('base64')
    .replace(/=/g, '')
    .replace(/\+/g, '-')
    .replace(/\//g, '_');

  return expectedSignature === signature;
}
const token = createJWT(payload, secret);
const isValid = verifyJWT(token, secret);
console.log('Token is valid:', isValid);
"}]

[{"title":"javascript","language":"javascript","content":"const payload = {
  user_id: 123,
  exp: Math.floor(Date.now() / 1000) + (60 * 60) // Hết hạn sau 1 giờ
};
const token = createJWT(payload, secret);
const isValid = verifyJWT(token, secret);
const decodedPayload = JSON.parse(Buffer.from(token.split('.')[1], 'base64').toString());
const isExpired = decodedPayload.exp < Math.floor(Date.now() / 1000);
console.log('Token is valid:', isValid);
console.log('Token is expired:', isExpired);
"}]

